INSERT INTO `tbl_message` (`id`, `language`, `translation`) VALUES
(33443, 'pl', 'Creators - Pomoc'),
(33444, 'pl', '<a name="how-to-host-a-website" class="anchor"></a>\n<h2>Jak założyć stronę www</h2>\n<div class="boxes-list">\n	<div class="row">\n		<div class="span3">\n			<div class="boxes-list-item">\n				<strong>1.</strong>\n				<img src="/images/creators/articles/hosting-pl.jpg" alt="" />\n				Na początek potrzebny jest hosting, który będzie mógł utrzymywać i wysyłać pliki html. \n				Każdy prekonfigurowany serwer jest wystarczający.\n			</div>\n		</div>\n		<div class="span3">\n			<div class="boxes-list-item">\n				<strong>2.</strong>\n				<img src="/images/creators/articles/www-pl.jpg" alt="" />\n				Przygotuj serwer i domenę. \n				Podążaj za instrukcjami usługodawców.\n				Jeśli masz problem, zapytaj usługodawcę o pomoc.\n				Możesz również skorzystać z <a href="strona/kontakt">naszej pomocy</a>.\n			</div>\n		</div>\n		<div class="span3">\n			<div class="boxes-list-item">\n				<strong>3.</strong>\n				<img src="/images/creators/articles/editor-pl.jpg" alt="" />\n				Użyj edytora Creators aby wygenerować stronę.\n			</div>\n		</div>\n		<div class="span3">\n			<div class="boxes-list-item">\n				<strong>4.</strong>\n				<img src="/images/creators/articles/download-pl.jpg" alt="" />\n				Pobierz wygenerowaną paczkę i wypakuj pliki.\n			</div>\n		</div>\n	</div>\n	<div class="row">\n		<div class="span3">\n			<div class="boxes-list-item">\n				<strong>5.</strong>\n				<img src="/images/creators/articles/upload.jpg" alt="" />\n				Wyślij wszystkie pliki na serwer. Umieść je we właściwym folderze o nazwie np. "public_html" lub "www".\n			</div>\n		</div>\n		<div class="span3">\n			<div class="boxes-list-item">\n				<strong>6.</strong>\n				<img src="/images/creators/articles/responsive.jpg" alt="" />\n				Uruchom przeglądarkę i zobacz jak działa strona!\n			</div>\n		</div>\n		<div class="span6">\n			<div class="boxes-list-item">\n				<br />\n				<div class="well top-10">\n					Masz problemy z którymś z powyższych kroków? <a href="/strona/kontakt">Zapytaj nas o pomoc</a>.\n				</div>\n			</div>\n		</div>\n	</div>\n</div>');
